				textures[i] = new Texture(
						Gdx.files.internal(textureFileNames[i]), i > 0 ? false
								: true);
			}
		}
		hasNormals = hasNormals();

		System.out.println("hasNormal: " + hasNormals);

		model.getBoundingBox(bounds);
