			manifestSummary = getManifestSummary(
				userId, groupId, parameterMap, newFile);
		}
		finally {
			if (rename) {
				newFile.renameTo(file);
			}
			else {
				FileUtil.delete(newFile);
			}
		}

		return manifestSummary;
	}

