   public String validate() throws LoginException
   {
      if (activationKey != null && !activationKey.isEmpty())
      {
         log.info("validate key:" + activationKey);
         String var = Base64UrlSafe.decode(activationKey);
         String[] array = var.split(";");
         String id = array[0];
