		for (int i = offset, n = offset + count - 3; i < n; i += 2) {
			p1x = vertices[i];
			p1y = vertices[i + 1];
			p2x = vertices[i + 2];
			p2y = vertices[i + 3];
			area += p1x * p2y - p2x * p1y;
		}
		p1x = vertices[count - 2];
		p1y = vertices[count - 1];
		p2x = vertices[0];
		p2y = vertices[1];
		return area + p1x * p2y - p2x * p1y < 0;
	}

