original_method:
public Sequence eval(
		Sequence contextSequence,
		Item contextItem)
		throws XPathException {
		if (contextItem != null)
			contextSequence = contextItem.toSequence();
		if (contextSequence.getItemType() != Type.NODE)
			return Sequence.EMPTY_SEQUENCE;
		String lang =
			getArgument(0)
				.eval(contextSequence)
				.getStringValue();
		QName qname = new QName("lang", context.getURIForPrefix("xml"), "xml");
		NodeSet attribs = context.getBroker().getElementIndex().getAttributesByName(contextSequence.toNodeSet().getDocumentSet(), qname, null);
		NodeSet temp = new ExtArrayNodeSet();
		NodeProxy p;
		String langValue;
		int hyphen;
		boolean include;
		for (Iterator i = attribs.iterator(); i.hasNext();) {
			include = false;
			p = (NodeProxy) i.next();
			langValue = p.getNodeValue();
			include = lang.equalsIgnoreCase(langValue);
			if (!include) {
				hyphen = langValue.indexOf('-');
				if (hyphen != -1) {
					langValue = langValue.substring(0, hyphen);
					include = lang.equalsIgnoreCase(langValue);
				}
			}
			if (include) {
				p.gid = XMLUtil.getParentId(p.getDocument(), p.gid);
				if (p.gid > -1) {
					p.setNodeType(Node.ELEMENT_NODE);
					temp.add(p);
				}
			}
		}
		if (temp.getLength() > 0) {
			NodeSet result =
				((NodeSet) contextSequence).selectAncestorDescendant(
					temp,
					NodeSet.DESCENDANT,
					true,
					false);
			for (Iterator i = result.iterator(); i.hasNext();) {
				p = (NodeProxy) i.next();
				p.addContextNode(p);
			}
			return result;
		}
		return Sequence.EMPTY_SEQUENCE;
	}

