public void run() {
            if(view == null) {
                log.warn("view is null, cannot send INFO message");
                return;
            }
            PhysicalAddress physical_addr=local_addr != null?
              (PhysicalAddress)down_prot.down(new Event(Event.GET_PHYSICAL_ADDRESS, local_addr)) : null;
