			sendBlockUpToChannel();
			onStartFlush(msg.getSrc(), fh);
		} else if (oldPhase == FlushPhase.FIRST_PHASE) {
			Address flushRequester = msg.getSrc();
			Address coordinator = null;
			synchronized (sharedLock) {
