                                        break;
                                    default:
                                        throw new IllegalStateException( "invalid direction: " + hero.getDirection() );
                                }
                            } else {
                                boolean containsPit = gameView.getPits().contains( new Pit( row, col ) );
                                boolean containsAliveWumpus = false;
