                                    if ( containsAliveWumpus ) {
                                        wumpus = "alive_wumpus";
                                    } else if ( containsDeadWumpus ) {
                                        wumpus = "dead_wumpus";
                                    }

                                    if ( containsGold ) {
                                        gold = "gold";
                                    }

                                    image = javax.imageio.ImageIO.read( getClass().getResource( pit + wumpus + gold + ".png" ) );
                                }
                            }
                        }
                        g.drawImage( image, y, x, 50, 50, this );
                        colPad = colPad + 3;
                    }
                    rowPad = rowPad + 3;
                }
            } catch ( Exception e ) {
                e.printStackTrace();
                throw new RuntimeException( e );
            }
        }

