      data.encoding = environmentSubstitute(meta.getEncoding());
      data.isEncoding = !Const.isEmpty(environmentSubstitute(meta.getEncoding()));

      data.byteBuffer  = ByteBuffer.allocateDirect(10000);

      return true;
    }
    return false;
  }

