					switch (sr.getState()) {
					case BUSY:
						break;
					case INIT:
						SyncRootTree srt = sr.getNextHash();
						if (null == srt)
							break;
						sr.pushNext(srt);
						sr.setState(SyncCompareState.PRELOAD);
						// Fall through
					case PRELOAD:
