                    NumberFormat nf = null;
                    if (locale == null) {
                        nf = NumberFormat.getNumberInstance();
                    } else {
                        nf = NumberFormat.getNumberInstance(locale);
                    }
                    Number tempNum = nf.parse(str);

                    return new Double(tempNum.doubleValue());
                } catch (ParseException e) {
