        actions.add(new AbstractAction(IdeBundle.message("updates.more.info.button")) {
          public void actionPerformed(ActionEvent e) {
            openDownloadPage();
          }
        });
      }
      else {
        for (ButtonInfo info : buttons) {
          actions.add(new ButtonAction(info));
        }
      }
    }
    else {
