  private boolean parseArguments(final String[] args) {
    final Args arg = new Args(args);
    boolean ok = true;
    try {
      while(arg.more() && ok) {
        if(arg.dash()) {
          final char c = arg.next();
          if(c == 'd') {
            dbindex = arg.num();
          } else if(c == 'q') {
            quindex = arg.num();
          } else if(c == 'r') {
            runs = arg.num();
