@Test
  public void testUkrainian() throws IOException {
    final Ukrainian ukrainian = new Ukrainian();
    final UppercaseSentenceStartRule rule = new UppercaseSentenceStartRule(TestTools.getEnglishMessages(), ukrainian);
    final JLanguageTool lt = new JLanguageTool(ukrainian);

    assertEquals(0, rule.match(lt.getAnalyzedSentence("      .")).length);

    final RuleMatch[] matches = rule.match(lt.getAnalyzedSentence("      ."));
    assertEquals(1, matches.length);
    assertEquals(1, matches[0].getSuggestedReplacements().size());
    assertEquals("", matches[0].getSuggestedReplacements().get(0));

    assertEquals(new ArrayList<RuleMatch>(), lt.check("    :

 1,

 2,

 3."));
    assertEquals(0, lt.check("    :

 1;

 2;

 3.").size());
    assertEquals(0, lt.check("    :

 1)  1;

2)  2;

3) 3.").size());
  }

