                    statement.close();
                    connection.close();
                } catch (Exception e) {
                     logger.log(Level.SEVERE, "Error closing database", e);
                }
            }
        } catch (Exception e) {
             logger.log(Level.SEVERE, "Error parsing Contacts to Blackboard", e);
        }

    }

