                    agentResource.start();
                    agentResource.configure(mockHost.getName(),
                            params);

                    newResources.put(agentResource, args);
                } catch (ConfigurationException e) {
                    s_logger
                    .error("error while configuring server resource"
                            + e.getMessage());
                }
            }
        }
        return newResources;
    }

