            System.out.printf(" - Target latency = %.2f ms\n", auto_tuning_target_latency_millis);
            System.out.printf(" - Adjustment rate = %.2f%%\n", auto_tuning_adjustment_rate * 100.0);
        }

        long phoneNumber;
        byte contestantNumber;

        long transactions_this_second = 0;
