                addArgIfNotEmpty( arguments, "-bottom", bottom );
                addArgIf( arguments, breakiterator, "-breakiterator", 1.4f );
                addArgIfNotEmpty( arguments, "-charset", charset );
                addArgIfNotEmpty( arguments, "-d", javadocDirectory.toString() );
