    public Resource getResource(ResourceResolver resourceResolver, String path) {
        File file;
        if (path.equals(providerRoot)) {
            file = providerFile;
        } else {
            String relPath = path.substring(providerRoot.length() + 1);
            file = new File(providerFile, relPath);
