        return false;

      final int remainingUsers = getUsers() > 0 ? removeUser() : 0;

      return iForce || (!(OGlobalConfiguration.STORAGE_KEEP_OPEN.getValueAsBoolean() && this instanceof OStorageEmbedded) && remainingUsers == 0);
    } finally {
      lock.releaseSharedLock();
    }
  }

