      System.out.println("Changed permission of " + path + " to "
          + Integer.toOctalString(newPermission));
    } catch (Exception e) {
      throw new IOException("Failed to changed permission of  " + path + " to "
          + Integer.toOctalString(newPermission) + " : " + e.getMessage());
    }
  }

