        try {
          if (scannerId != -1L) {
            server.close(scannerId);
          }
        } catch (IOException e) {
          e.printStackTrace();
        }
        scannerId = -1L;
      }
      if (LOG.isDebugEnabled()) {
        LOG.debug("scan of meta region " + region.regionName + " complete");
      }
      return scannedRegion;
    }

