            if (isEligibleForCaching(directoryFileTree)) {
                final String absolutePath = directoryFileTree.getDir().getAbsolutePath();
                VisitedTree cachedTree = allowReuse ? cachedTrees.getIfPresent(absolutePath) : null;
                if (cachedTree != null) {
