        FieldSource field = clazz.getField(endpointInstanceName);
        AnnotationSource annotation;
        if (field == null) {
            field = clazz.addField();
            field.setName(endpointInstanceName);
            field.setType("org.apache.camel.Endpoint");
            field.setPrivate();
