        }
        if (cdi) {
            annotation = field.getAnnotation("org.apache.camel.cdi.Uri");
            if (annotation == null) {
                if (!field.hasAnnotation("javax.inject.Inject")) {
                    field.addAnnotation("javax.inject.Inject");
                }
