		synchronized(this) {
			boolean isInPeers = false;
			for(int i = 0; i < connectedPeers.length; i++) {
				if(connectedPeers[i] == pn)
					isInPeers = true;
			}
			if(!isInPeers)
				return false;
			// removing from connectedPeers
			ArrayList<PeerNode> a = new ArrayList<PeerNode>();
			for(PeerNode mp : myPeers) {
				if((mp != pn) && mp.isRoutable())
					a.add(mp);
