@Override
	public int read(byte[] b, int off, int len) throws IOException {
		int ret = 0;
		while (len > 0) {
			if (off + len >= b.length) {
				if (off >= b.length)
					break;
				len = b.length - off;
			}
			if (_datagram.hasRemaining()) {
				int size = _datagram.remaining() > len ? len : _datagram.remaining();
				_datagram.get(b, off, size);
				ret += len;
				off += size;
				len -= size;
			} else {
				int tmpRet = fill();
				if (tmpRet <= 0) {
					if (ret == 0)
						ret = tmpRet;
					break;
				}
			}
		}
		return ret;
	}

