	 * to step 1. <br/>
	 */
	public String getRealSclass() {
		final String mold = super.getMold();
		String scls = null;
		if (mold.equals("v30")) {
			scls = super.getSclass();
			if (scls == null)
				scls = "tab";
			if (isDisabled())
				scls = scls + "dis";
			return isSelected() ? scls + "sel" : scls;
		} else {
			scls = super.getMoldSclass();
			if (scls == null)
				scls = "z-tab";
			if (isDisabled())
				scls = scls + "dis";
			return isSelected() ? scls + "sel" : scls;
		}
	}

