                if (opponent == null || !paid) {
                    if (game.getState().getZone(creature.getId()).equals(Zone.GRAVEYARD)) {
                        controller.moveCardToHandWithInfo(creature, source.getSourceId(), game, Zone.GRAVEYARD);
                    }
                }                
            }
            return true;
        }
        return false;
    }

