						if (eObjectDescription.getEObjectURI().equals(eObjectURI)) {
							new ReferenceSearchViewTreeNode(resourceNode, eObjectURI, eObjectDescription);
						}
					}
				}
			}
			viewer.refresh();
		}
	}

