private boolean isPrefixForEditor(KeyCombination keys)
   {
      if (editorMode_ == KeyboardShortcut.MODE_EMACS)
      {
         if (keys.isCtrlPressed())
         {
            int keyCode = keys.getKeyCode();
            return keyCode == KeyCodes.KEY_C ||
                   keyCode == KeyCodes.KEY_X;
         }
      }

      return false;
   }

