        for (Item item : items) {
            if (item.isFailure()) {
                builder.startObject();
                builder.field(Fields.ERROR, item.getFailureMessage());
                builder.endObject();
            } else {
                builder.startObject();
                item.getResponse().toXContent(builder, params);
                builder.endObject();
            }
        }
        builder.endArray();
