                oldVal0 = cctx.cacheObjectContext().unwrapPortableIfNeeded(oldVal, keepPortable, false);
            }
            catch (Exception e) {
                if (!cctx.cacheObjectContext().processor().isPortableEnabled(cctx.config()))
                    throw e;

                if (log.isDebugEnabled())
