/**
   * Visits files in a directory or steps further down.
   * @param d the directory to be visited.
   * @throws IOException I/O exception
   */
  private void parse(final File d) throws IOException {
    final File[] files = d.listFiles();
    if(files == null) return;

    for(final File f : files) {
      if(!valid(f)) continue;

      if(f.isDirectory()) {
        if (wbacking)
          new File(backingroot
            + f.getAbsolutePath().substring(importroot)).mkdir();
        dir(f);
      } else {
        if (wbacking)
          copy(f.getAbsoluteFile(), new File(backingroot
            + f.getAbsolutePath().substring(importroot)));
        file(f);
      }
    }
  }

